import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { addData } from '../actions/dataActions';
import axios from 'axios';

function Form() {
  const dispatch = useDispatch();
  const [employee, setEmployee] = useState('');
  const [noBarang, setNoBarang] = useState(''); // Kolom "No Barang" yang akan diisi otomatis
  const [namaBarang, setNamaBarang] = useState('');
  const [harga, setHarga] = useState(''); // Kolom "Harga"
  const [diskon, setDiskon] = useState('');
  const [total, setTotal] = useState('');
  const [employeeOptions, setEmployeeOptions] = useState([]);

  useEffect(() => {
    // Mengambil data Employee dari API
    axios.get('https://financed.4netps.co.id/ujian/employee')
      .then((response) => {
        setEmployeeOptions(response.data);
      })
      .catch((error) => {
        console.error('Gagal mengambil data Employee: ' + error);
      });
  }, []);

  // Menggunakan useEffect untuk mengambil data Employee ketika Employee dipilih
  useEffect(() => {
    if (employee) {
      // Mengambil data Employee dari API berdasarkan Employee yang dipilih
      axios.get(`https://financed.4netps.co.id/ujian/employee/${employee}`)
        .then((response) => {
          setNoBarang(response.data.CODE_DEFT);
        })
        .catch((error) => {
          console.error('Gagal mengambil data "No Barang": ' + error);
        });
    }
  }, [employee]);

  const handleSave = () => {
    // Dispatch action to add data to Redux store
    dispatch(addData({ employee, noBarang, namaBarang, harga, diskon, total }));
  };

  return (
    <div className="form-container">
      <div className="form-group">
        <label for="employee">Employee:</label>
        <select id="employee" onChange={(e) => setEmployee(e.target.value)} value={employee}>
          <option value="">Pilih Employee</option>
          {employeeOptions.map((option) => (
            <option key={option.ID} value={option.NAME}>
              {option.NAME}
            </option>
          ))}
        </select>
      </div>

      <div className="form-group">
        <label for="noBarang">No Barang:</label>
        <input type="text" id="noBarang" value={noBarang} readOnly />
      </div>

      <div className="form-group">
        <label for="namaBarang">Nama Barang:</label>
        <input type="text" id="namaBarang" value={namaBarang} onChange={(e) => setNamaBarang(e.target.value)} />
      </div>

      <div className="form-group">
        <label for="harga">Harga:</label>
        <input type="text" id="harga" value={harga} onChange={(e) => setHarga(e.target.value)} />
      </div>

      <div className="form-group">
        <label for="diskon">Diskon:</label>
        <input type="text" id="diskon" value={diskon} onChange={(e) => setDiskon(e.target.value)} />
      </div>

      <div className="form-group">
        <label for="total">Total:</label>
        <input type="text" id="total" value={total} readOnly />
      </div>

      <button onClick={handleSave}>Save</button>
    </div>
  );
}

export default Form;
