// src/components/Form.js
import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addData } from '../actions/dataActions';

function Form() {
  const dispatch = useDispatch();
  const [employee, setEmployee] = useState('');
  const [noBarang, setNoBarang] = useState('');
  const [namaBarang, setNamaBarang] = useState('');
  const [diskon, setDiskon] = useState('');
  const [total, setTotal] = useState('');

  const handleSave = () => {
    // Dispatch action to add data to Redux store
    dispatch(addData({ employee, noBarang, namaBarang, diskon, total }));
  };

  return (
    <div>
      <select onChange={(e) => setEmployee(e.target.value)} value={employee}>
        <option value="employee1">Employee 1</option>
        <option value="employee2">Employee 2</option>
        {/* Add more employee options */}
      </select>
      <input type="text" value={noBarang} readOnly />
      <input type="text" value={namaBarang} onChange={(e) => setNamaBarang(e.target.value)} />
      <input type="text" value={diskon} onChange={(e) => setDiskon(e.target.value)} />
      <input type="text" value={total} readOnly />
      <button onClick={handleSave}>Save</button>
    </div>
  );
}

export default Form;
