import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { addData } from '../actions/dataActions';
import axios from 'axios';
import Select from 'react-select';

function Form() {
  const dispatch = useDispatch();
  const [employee, setEmployee] = useState('');
  const [noBarang, setNoBarang] = useState(''); // Kolom "No Barang" yang akan diisi otomatis
  const [namaBarang, setNamaBarang] = useState('');
  const [diskon, setDiskon] = useState('');
  const [total, setTotal] = useState('');
  const [employeeOptions, setEmployeeOptions] = useState([]);

  useEffect(() => {
    // Mengambil data dari API
    axios.get('https://financed.4netps.co.id/ujian/employee')
      .then((response) => {
        setEmployeeOptions(response.data);
        
      })
      .catch((error) => {
        console.error('Gagal mengambil data employee: ' + error);
      });
  }, []);
  
  const fetchData = async () => {
    try {
      const response = await axios.get(`https://financed.4netps.co.id/ujian/employee/${selectedOption}`);
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  
  const handleEmployeeChange = (selectedEmployee) => {
    setEmployee(selectedEmployee);
  
    // Mengambil data "No Barang" dari API berdasarkan "Employee" yang dipilih
    axios.get(`https://financed.4netps.co.id/ujian/employee/${selectedEmployee}`)
      .then((response) => {
        setNoBarang(response.data);
      })
      .catch((error) => {
        console.error('Gagal mengambil data "No Barang": ' + error);
      });
  };

  const handleSave = () => {
    // Dispatch action to add data to Redux store
    dispatch(addData({ employee, noBarang, namaBarang, diskon, total }));
  };

  return (
    <div>
      <select onChange={(e) => handleEmployeeChange(e.target.value)} value={employee}>
        <option value="">Pilih Employee</option>
        {employeeOptions.map((option) => (
          <option key={option.ID} value={option.NAME}>
            {option.NAME} 
            
          </option>
        ))}
      </select>
      <input type="text" value={noBarang} readOnly />
      <input type="text" value={namaBarang} onChange={(e) => setNamaBarang(e.target.value)} />
      <input type="text" value={diskon} onChange={(e) => setDiskon(e.target.value)} />
      <input type="text" value={total} readOnly />
      <button onClick={handleSave}>Save</button>
    </div>
  );
}

export default Form;
